#include <stdio.h>
int main(){unsigned long int z=1000000,A[100001]={0},B1[100001]={0},B2[100001]={0},i,n=0,o=0,p=0,t,y,x,k;for(i=1;i<=z;i++){n+=i*(i+1)/2;if(i%10==0)A[i/10]=n;if(i%2==0){o+=i*(i+1)/2;if(i%20==0)B2[i/20]=o;}else{p+=i*(i+1)/2;if((i-1)%20==0)B1[(i-1)/20]=p;}}scanf("%uld",&t);while(t--){scanf("%uld",&n);i=n/10;x=A[i];if(n%10!=0){i*=10;i++;while(i<=n){x+=i*(i+1)/2;i++;}}k=n/2;i=k/20;if(n%2==0){y=B1[i];if(k%20!=1){i=i*20+1;i+=2;k-=(i-1)/2;while(k--){y+=i*(i+1)/2;i+=2;}}}else{y=B2[i];if(k%20!=0){i*=20;i+=2;k=k-i/2+1;while(k--){y+=i*(i+1)/2;i+=2;}}}x=x+y;printf("%u\n",x);}return 0;}
